import React from 'react';
import './Post.css';
const getTime = (datetime) =>{
    let curTime=new Date();
    let diff=curTime-datetime;
    if (diff/(1000*60*60*24)>1) return `${parseInt(diff/(1000*60*60*24))} days ago`;
    if (diff/(1000*60*60*24)===1) return `${parseInt(diff/(1000*60*60*24))} day ago`;
    if (diff/(1000*60*60)>=1) return `${parseInt(diff/(1000*60*60))} hours ago`;
    if (diff/(1000*60*60)>0) return `${parseInt(diff/(1000*60*60))} hour ago`;
    if (diff/(1000*60*60*24)>=1) return `${parseInt(diff/(1000*60*60*24))} minute ago`;
    if (diff/(1000*60*60*24)>0) return `${parseInt(diff/(1000*60*60*24))} minute ago`;
}
const Post = (props) =>{
    let post=props.post;
    return(
        <div id={`post-${props.id}`} className="post-component">
            <div className="post-component__title">{post.title} 
            <span>{post.date.toString()}</span>
            </div>
            <div className="post-component__content"> by <span>{post.author}</span> <br></br>
            {post.content.slice(0,Math.min(100,post.content.length))}
            </div>
        </div>
    )
}

export default Post;
