import React from 'react';
import './Post.css';
const getTime = (datetime) =>{
    let curTime=new Date();
    console.log(curTime);
    console.log(datetime);
    let diff=curTime-datetime;
    console.log(diff);
    if (parseInt(diff/(1000*60*60*24))>=2) return `${parseInt(diff/(1000*60*60*24))} days ago`;
    if (parseInt(diff/(1000*60*60*24))===1) return `a day ago`;
    if (parseInt(diff/(1000*60*60))>=2) return `${parseInt(diff/(1000*60*60*24))} hours ago`;
    if (parseInt(diff/(1000*60*60))===1) return `an hour ago`;
    if (parseInt(diff/(1000*60))>=2) return `${parseInt(diff/(1000*60*60*24))} minutes ago`;
    if (parseInt(diff/(1000*60))===1) return `${parseInt(diff/(1000*60*60*24))} minute ago`;
    return `seconds ago`;


}
const Post = (props) =>{
    let post=props.post;
    return(
        <div id={`post-${props.id}`} className="post-component">
            <div className="post-component__title">{post.title} 
            <span>{getTime(post.date)}</span>
            </div>
            <div className="post-component__content"> by <span>{post.author}</span> <br></br>
            {post.content.slice(0,Math.min(100,post.content.length))}
            </div>
        </div>
    )
}

export default Post;
